from openpyxl import load_workbookimport pandas as pdfrom Categoriser import SimpleCategoriserfrom Filters import RowFilter, ColoumnFilterfrom Item import Itemdef get_data(path):    wb = load_workbook(path)    data = pd.DataFrame(data=wb.active.values)    data.columns = data.iloc[0]    return datadef filter_data(data):    row_filter = RowFilter()    col_filter = ColoumnFilter()    data = row_filter.filter(data)    data = col_filter.filter(data)    return dataif __name__ == "__main__":    source_path = r"data/Duct Fitting Schedule Medium Pressure Insulated Rect.xlsx"    boq_data = get_data(source_path)    boq_data = filter_data(boq_data)    boq = [        Item(row["Size"], row["Count"], row["Area"], row["Surface Area"], SimpleCategoriser())        for _, row in boq_data.iterrows()    ]    summarised_boq = {        category: sum([item.count * item.boq_area for item in boq if item.category == category])        for category in range(1, 6)    }    print(summarised_boq)