import osfrom src.boq import Boqfrom src.categoriser import SimpleCategoriserfrom src.data_handler import DataHandlerif __name__ == "__main__":    rates = {1: 1, 2: 2.5, 3: 4, 4: 5, 5: 6}    handler = DataHandler()    input_files = os.listdir('data/')    for input_file in input_files:        source_path = fr"data/{input_file}"        print(source_path)        data = handler.data(source_path)        boq = Boq(data=data, rates=rates, categoriser=SimpleCategoriser)        summarised_boq = boq.summarised_boq()        output_file = os.path.basename(source_path).split('.')[0]        handler.save(summarised_boq, f"outputs/csv/BOQ_{output_file}.csv")        print(summarised_boq)