import osfrom src.categoriser import SimpleCategoriserfrom src.item import Itemfrom src.data_handler import DataHandlerif __name__ == "__main__":    source_path = r"data\Duct Schedule Low Pressure Insulated Cladded Rect.csv"    handler = DataHandler()    boq_data = handler.data(source_path)    boq = [        Item(row["Size"], row["Count"], row["Area"], row["Surface Area"], SimpleCategoriser())        for _, row in boq_data.iterrows()    ]    summarised_boq = {}    for item in boq:        summarised_boq.setdefault(item.category, 0)        summarised_boq[item.category] += item.count * item.boq_area    output_file = os.path.basename(source_path).split('.')[0]    handler.save(summarised_boq, f"outputs/csv/BOQ_{output_file}.csv")    print(summarised_boq)