import pandas as pdfrom src.boq import Boqfrom src.hvac_rectangular_categoriser import HvacRectangularCategoriserclass TestBoq:    def test_summary_when_called_with_valid_data_calculates_correct_subtotal(self):        rates = {1: 1, 5: 10}        data = pd.DataFrame(data=[['200x250-200x250', '4', '0', '20'], ['3000x3250-3200x3250', '4', '0', '20']],                            columns=['Size', 'Count', 'Area', 'Surface Area'])        boq = Boq(data=data, rates=rates, categoriser=HvacRectangularCategoriser)        result = boq.summary()['total']['cost']        assert result == 880